mapper.select:

select 

from role role
where role.id = #{id,jdbcType=BIGINT}

mapper.insert:


  SELECT LAST_INSERT_ID()

insert into role (role_name, role_sign, description
  )
values (#{roleName,jdbcType=VARCHAR}, #{roleSign,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}
  )

